$(document).ready(function(){$(".st-nav-toggle").click(function(e){e.preventDefault(),$(".st-nav").toggleClass("open")}),$(".st-nested-nav").click(function(e){e.preventDefault(),$(this).find(".st-sub-menu").toggleClass("open")}),$(".st-nested-nav").focusout(function(e){e.preventDefault(),0==$(this).has(document.activeElement).length&&$(this).find(".st-sub-menu").removeClass("open")})});